package com.likes.common.service.uploadFile;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;import java.util.List;import java.util.Map;public interface UplaodFileSevice {    /**     * 公共的 文件上传工具类     * @param files 文件流     * @param moduleKey 对应的模块参数 去系统参数配置查询 例如-----等级图标：LEVEL_IMG_URL     * @param type 文件类型 0：图片、1：视频、2：其他文件类型     * @return     * @throws IOException     */    List<String> uploadFile(MultipartFile[]  files, String moduleKey, Integer type) throws IOException;}